import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { CdbResgateRequest } from 'src/app/models/cdb-resgate-request.model';
import { ResgateCdbService } from 'src/app/services/resgate-cdb.service';


@Component({
  selector: 'app-resgate-cdb',
  templateUrl: './resgate-cdb.component.html',
  styleUrls: ['./resgate-cdb.component.css'],
})
export class ResgateCdbComponent {
  constructor(private resgateCdbService: ResgateCdbService, private formBuilder: FormBuilder) 
  { }

  formulario!: FormGroup;
  canShow:boolean = false;
  valorBruno: number = 0;
  valorLiquido: number = 0;


  ngOnInit(): void {
    this.canShow = false;
     this.formulario = this.formBuilder.group({
     valorResgate: [null, Validators.required],
     quantidadeMes: [null, Validators.required]
     })
  }

  obj : CdbResgateRequest = new CdbResgateRequest();


  resgatarCdb() {

    this.obj.valorResgate = this.formulario.value.valorResgate;
    this.obj.qtdMes = this.formulario.value.quantidadeMes;;

    console.log('meu click');
    console.log(this.formulario.value);

    this.resgateCdbService.resgatarDois(this.obj).subscribe(
      (data) => {       

        //@ts-ignore
        this.valorBruno = data.valorBruto;
        //@ts-ignore
        this.valorLiquido = data.valorLiquido;
        this.canShow = true;
        this.formulario.reset();
      },
      (error) => {
        console.log('retono do serviço com erro');
        console.log(error);
        this.canShow = false;
      }
    );

  }

  lsitarCdb(){

    console.log('meu click');

    this.resgateCdbService.resgatar().subscribe(
      (data) => {
        this.valorBruno = data.valorBruno;
        this.valorLiquido = data.valorLiquido;
      },
      (error) => {
        console.log('retono do serviço');
        console.log(error);
      }
    );
  }
}
<form class="form-horizontal" [formGroup] ="formulario">
  <div class="input-panel">
    <h2 class="panel-title">CDB Resgate</h2>
  <div class="mb-3">
    <label for="exampleFormControlInput1" class="form-label">Valor de Resgate</label>
    <input type="number" formControlName="valorResgate" class="form-control" id="valorResgate" placeholder="Informe o valor do resgate">
    <div *ngIf="form.controls.valorResgate.invalid && (form.controls.valorResgate.dirty || form.controls.valorResgate.touched)">
      <div *ngIf="form.controls.valorResgate.errors?.invalidDecimal" class="text-danger">Valor deve ser maior que 0.</div>
    </div>
  </div>
  <div class="mb-3">
    <label for="exampleFormControlTextarea1" class="form-label">Quantidade de Meses</label>
    <input type="number" formControlName="quantidadeMes" class="form-control" id="exampleFormControlInput1" placeholder="informe a quantidade de meses">
  </div>

    <button (click)="resgatarCdb()" class="btn btn-primary">Resgatar</button>
  </div>
</form>

<br>

  <div *ngIf="canShow" class="alert alert-success" role="alert">
    <h4 class="alert-heading">Resultado do Investimento</h4>
    <p>Resultado Bruno : {{valorBruno}}</p>
    <hr>
    <p>Resultado líquido : {{valorLiquido}}</p>
  </div>
