import { Component } from '@angular/core';
import { CdbResgateRequest } from 'src/app/models/cdb-resgate-request.model';
import { ResgateCdbService } from 'src/app/services/resgate-cdb.service';


@Component({
  selector: 'app-resgate-cdb',
  templateUrl: './resgate-cdb.component.html',
  styleUrls: ['./resgate-cdb.component.css'],
})
export class ResgateCdbComponent {
  constructor(private resgateCdbService: ResgateCdbService) {}

  valorResgate: string = '';
  quantidadeMes: string = '';

  obj : CdbResgateRequest = new CdbResgateRequest();

  valorBruno: number = 0;
  valorLiquido: number = 0;
  CdbResgateRequest = {
    valorResgate:10,
    qtdMes:6
  }





  resgatarCdb() {

    this.obj.valorResgate = 500;
    this.obj.qtdMes = 6;

    console.log('meu click');
    console.log(CdbResgateRequest);

    this.resgateCdbService.resgatarDois(this.obj).subscribe(
      (data) => {
        console.log('Sucesso');
        console.log(data)
        //this.valorBruno = data.valorBruno;
        //this.valorLiquido = data.valorLiquido;
      },
      (error) => {
        console.log('retono do serviço com erro');
        console.log(error);
      }
    );

  }

  lsitarCdb(){

    console.log('meu click');

    this.resgateCdbService.resgatar().subscribe(
      (data) => {
        this.valorBruno = data.valorBruno;
        this.valorLiquido = data.valorLiquido;
      },
      (error) => {
        console.log('retono do serviço');
        console.log(error);
      }
    );
  }
}
<div class="input-panel">
    <h2 class="panel-title">CDB Resgate</h2>
    <form>
        <label for="valorResgate">Inform o valor de resgate:</label>
         <input type="text" id="valorResgate" [(ngModel)]="valorResgate" name="valorResgate" step="0.01" 
    placeholder="Informe o valor do resgate">

    <label for="quantidadeMes">Valor Inteiro:</label>
    <input type="text" id="quantidadeMes" [(ngModel)]="quantidadeMes" name="quantidadeMes" step="1">

    <input type="submit" value="Resgatar" />

    <button (click)="resgatarCdb()">Resgatar two</button>

    </form>

    <p>O valor de resgate é {{valorResgate}}</p>
    <p> A quantidade de meses é {{quantidadeMes}}</p>
  </div>

  <form>
    <div class="input-panel">
      <h2 class="panel-title" [ngStyle]="{'text-align': center}">CDB Resgate</h2>
      <div class="mb-3">
        <label for="exampleInputEmail1" class="form-label">Email address</label>
        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
      </div>

      <div class="mb-3">
        <label for="exampleInputPassword1" class="form-label">Password</label>
        <input type="password" class="form-control" id="exampleInputPassword1">
      </div>

      <button type="submit" class="btn btn-primary">Submit</button>
    </div>


  </form>
