import { Component } from '@angular/core';
import { ResgateCdbService } from 'src/app/services/resgate-cdb.service';

@Component({
  selector: 'app-resgate-cdb',
  templateUrl: './resgate-cdb.component.html',
  styleUrls: ['./resgate-cdb.component.css'] 
})
export class ResgateCdbComponent {

  constructor(private resgateCdbService: ResgateCdbService){}

  valorResgate: string = '';
  quantidadeMes: string = '';


  resgatarCdb(){
    this.resgateCdbService.resgatar();
  }

}
<div class="input-panel">
    <h2 class="panel-title">CDB Resgate</h2>
    <form>
        <label for="valorResgate">Inform o valor de resgate:</label>
         <input type="text" id="valorResgate" [(ngModel)]="valorResgate" name="valorResgate" step="0.01" 
    placeholder="Informe o valor do resgate">

    <label for="quantidadeMes">Valor Inteiro:</label>
    <input type="text" id="quantidadeMes" [(ngModel)]="quantidadeMes" name="quantidadeMes" step="1">

    <input type="submit" value="Resgatar" />

    </form>

    <p>O valor de resgate é {{valorResgate}}</p>
    <p> A quantidade de meses é {{quantidadeMes}}</p>
  </div>
