import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { CdbResgateRequest } from 'src/app/models/cdb-resgate-request.model';
import { ResgateCdbService } from 'src/app/services/resgate-cdb.service';


@Component({
  selector: 'app-resgate-cdb',
  templateUrl: './resgate-cdb.component.html',
  styleUrls: ['./resgate-cdb.component.css'],
})
export class ResgateCdbComponent {
  constructor(private resgateCdbService: ResgateCdbService, private formBuilder: FormBuilder) 
  { }


  formulario!: FormGroup;
  canShow:boolean = true;
  valorBruno: number = 0;
  valorLiquido: number = 0;


  ngOnInit(): void {

     this.formulario = this.formBuilder.group({
     valorResgate: [0],
     quantidadeMes: [0]
     })
  }


  obj : CdbResgateRequest = new CdbResgateRequest();


  CdbResgateRequest = {
    valorResgate:10,
    qtdMes:6
  }





  resgatarCdb() {

    this.obj.valorResgate = 500;
    this.obj.qtdMes = 6;

    console.log('meu click');
    //console.log(this.formModal.value);

    this.resgateCdbService.resgatarDois(this.obj).subscribe(
      (data) => {
        console.log('Sucesso');
        console.log(data)

        //@ts-ignore
        this.valorBruno = data.valorBruto;

        //@ts-ignore
        this.valorLiquido = data.valorLiquido;

        this.canShow = true;
      },
      (error) => {
        console.log('retono do serviço com erro');
        console.log(error);
      }
    );

  }

  lsitarCdb(){

    console.log('meu click');

    this.resgateCdbService.resgatar().subscribe(
      (data) => {
        this.valorBruno = data.valorBruno;
        this.valorLiquido = data.valorLiquido;
      },
      (error) => {
        console.log('retono do serviço');
        console.log(error);
      }
    );
  }
}
<form [formGroup]="formulario">
  <div class="input-panel">
    <h2 class="panel-title">CDB Resgate</h2>

      <label for="exampleInputEmail1" class="form-label"
        >Valor de Resgate</label
      >
      <input
        type="number"
        class="form-control"       
        placeholder="Informe o valor de resagte"    
      />
      <div id="valorResgate" class="form-text">Valor obrigatório</div>


    <!-- <div class="mb-3">
      <label for="quantidadeMes" class="form-label">Quantidade de Mês</label>
      <input
        type="number"
        class="form-control"
        id="quantidadeMes"
        placeholder="Informe a quantidade de meses",
        formControlName="valorResgate"
      />
    </div>   -->
    <!-- <button (click)="resgatarCdb()" class="btn btn-primary">Resgatar</button> -->
  </div>
</form>

<br>

  <div *ngIf="canShow" class="alert alert-success" role="alert">
    <h4 class="alert-heading">Resultado do Investimento</h4>
    <p>Resultado Bruno : {{valorBruno}}</p>
    <hr>
    <p>Resultado líquido do investimento : {{valorLiquido}}</p>
  </div>
